function enemyAction() {

  const random = Math.floor(Math.random() * enemies.length);
  const enemy = enemies[random];
  let enemyHP = 50;

  console.log(enemy + "ðŸ· is after you");

    function battle() {
    readline.question('Fight (f) or Run (r)? ', (choice) => {
      if (enemyHP <= 0) {
        console.log(`You have defeated ${enemy}! ...for now`);
        const hpGain = 20;
        playerHP += hpGain;
        console.log(`You gained ${hpGain} HP!`);
        const specialItem = `ðŸ˜‰Special Item Pre Core Pillz; ðŸ’Š PCP ðŸ˜‰ for short: PCPðŸ’Š${playerInventory.length + 1}`;
        playerInventory.push(specialItem);
        console.log(`You obtained a ðŸ˜‰special item: ${specialItem}!`);
        console.log(`Press 'w' to walk, 'p' for status, or 'q' to quit`);
        return;
      }

      if (choice === 'f') {
        const minDamage = 10;
        const maxDamage = 20;
        const damage = Math.floor(Math.random() * (maxDamage - minDamage + 1)) + minDamage;
        console.log(`You attacked ${enemy} and dealt ${damage} damage!`);
        enemyHP -= damage;

        if (enemyHP <= 0) {
          return battle();
        } else {
          const enemyDamage = Math.floor(Math.random() * (maxDamage - minDamage + 1)) + minDamage;
          console.log(`${enemy} attacked you and dealt ${enemyDamage} damage!`);
          playerHP -= enemyDamage;

          if (playerHP <= 0) {
            console.log(`You have been relieved of life by ðŸ· ${enemy}!`);
            console.log('ðŸ©¸!GAMEðŸ·OVER!ðŸ©¸');
            isAlive = false;
            return;
          } else {
            console.log(`You have ${playerHP} HP left`);
            console.log(`${enemy} has ${enemyHP} HP left`);
            return battle();
          }
        }
      } else if (choice === 'r') {
        const escape = Math.random() < 0.5;
        if (escape) {
          console.log(`${userName} breathes heavily, ðŸ’­Safe for now!`);
          console.log(`Press 'w' to walk, 'p' for status, or 'q' to quit`);
          return;
        } else {
          const minDamage = 10;
          const maxDamage = 20;
          const enemyDamage = Math.floor(Math.random() * (maxDamage - minDamage + 1)) + minDamage;
          console.log(`${enemy} attacked you and dealt ${enemyDamage} damage!`);
          playerHP -= enemyDamage;

          if (playerHP <= 0) {
            console.log(`You have been defeated by ${enemy}!`);
            console.log('ðŸ©¸!GAMEðŸ·OVER!ðŸ©¸');
            isAlive = false;
            return;
          } else {
            console.log(`You have ${playerHP} HP left`);
            console.log(`${enemy} has ${enemyHP} HP left`);
            return battle();
          }
        }
              }else {
                // Invalid Option
                console.log('Invalid Option');
                return battle();
                
              }
      })
    }

  }; 
  enemyAction()
       battle()
   


    
